import React, { Component } from 'react';
import CardList from './CardList';
import SearchBox from './SearchBox';
import { robots } from './robots';



class App extends Component {
    constructor() {
        super()
        this.state = {
            robots: robots,
            searchfield: ''
        }
    }

    onSearchChange = (event) => {
        console.log(event.target.value);
        this.setState({ searchfield: event.target.value })

    }


    render() {
        console.log(robots)
        const filteredRobots = this.state.robots.filter(robots =>{
            console.log(robots.name.toLowerCase())
            console.log("Atencion  " + robots.name.toString().toLowerCase().includes(this.state.searchfield).toString().toLowerCase())
            return robots.name.toString().toLowerCase().includes(this.state.searchfield).toString().toLowerCase();
        })
        console.log("fliteredRobots " + filteredRobots[0].name);
        return (
            <div className="tc">
                <h1>RoboFriends</h1>
                <SearchBox searchChange={this.onSearchChange} />
                <CardList robots ={filteredRobots} />
            </div> 
        );
    }

}

export default App;
